import{_ as t}from"./chunks/admin-ui-redis-stats.3a9d7244.js";import{_ as e,o as a,c as s,a as o}from"./app.2aea72c0.js";const v='{"title":"Redis Stats","description":"","frontmatter":{"slug":"stats","title":"Redis Stats"},"headers":[{"level":2,"title":"Redis Stats in Admin UI Dashboard","slug":"redis-stats-in-admin-ui-dashboard"},{"level":2,"title":"Log to Console","slug":"log-to-console"}],"relativePath":"redis/stats.md"}',d={},r=o('<p>The <a href="https://github.com/ServiceStack/ServiceStack.Redis/blob/master/src/ServiceStack.Redis/RedisStats.cs" target="_blank" rel="noopener noreferrer">RedisStats</a> class provides better visibility and introspection into your running instances:</p><table><tr><td><b>TotalCommandsSent</b></td> <td>Total number of commands sent</td></tr><tr><td><b>TotalFailovers</b></td> <td>Number of times the Redis Client Managers have FailoverTo() either by sentinel or manually</td></tr><tr><td><b>TotalDeactivatedClients</b></td> <td>Number of times a Client was deactivated from the pool, either by FailoverTo() or exceptions on client</td></tr><tr><td><b>TotalFailedSentinelWorkers</b></td> <td>Number of times connecting to a Sentinel has failed</td></tr><tr><td><b>TotalForcedMasterFailovers</b></td> <td>Number of times we&#39;ve forced Sentinel to failover to another master due to consecutive errors</td></tr><tr><td><b>TotalInvalidMasters</b></td> <td>Number of times a connecting to a reported Master wasn&#39;t actually a Master</td></tr><tr><td><b>TotalNoMastersFound</b></td> <td>Number of times no Masters could be found in any of the configured hosts</td></tr><tr><td><b>TotalClientsCreated</b></td> <td>Number of Redis Client instances created with RedisConfig.ClientFactory</td></tr><tr><td><b>TotalClientsCreatedOutsidePool</b></td> <td>Number of times a Redis Client was created outside of pool, either due to overflow or reserved slot was overridden</td></tr><tr><td><b>TotalSubjectiveServersDown</b></td> <td>Number of times Redis Sentinel reported a Subjective Down (sdown)</td></tr><tr><td><b>TotalObjectiveServersDown</b></td> <td>Number of times Redis Sentinel reported an Objective Down (odown)</td></tr><tr><td><b>TotalRetryCount</b></td> <td>Number of times a Redis Request was retried due to Socket or Retryable exception</td></tr><tr><td><b>TotalRetrySuccess</b></td> <td>Number of times a Request succeeded after it was retried</td></tr><tr><td><b>TotalRetryTimedout</b></td> <td>Number of times a Retry Request failed after exceeding RetryTimeout</td></tr><tr><td><b>TotalPendingDeactivatedClients</b></td> <td>Total number of deactivated clients that are pending being disposed</td></tr></table><h2 id="redis-stats-in-admin-ui-dashboard" tabindex="-1">Redis Stats in Admin UI Dashboard <a class="header-anchor" href="#redis-stats-in-admin-ui-dashboard" aria-hidden="true">#</a></h2><p>These Stats are displayed in the <a href="/admin-ui-redis#redis-stats-on-dashboard">Admin UI Dashboard</a></p><p><a href="/admin-ui-redis#redis-stats-on-dashboard"><img src="'+t+`" alt=""></a></p><h2 id="log-to-console" tabindex="-1">Log to Console <a class="header-anchor" href="#log-to-console" aria-hidden="true">#</a></h2><p>Alternatively you can get and print a dump of all the stats at anytime with:</p><div class="language-csharp"><pre><code>RedisStats<span class="token punctuation">.</span><span class="token function">ToDictionary</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">PrintDump</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><p>And Reset all Stats back to <code>0</code> with <code>RedisStats.Reset()</code>.</p>`,9),n=[r];function i(l,c,u,b,p,m){return a(),s("div",null,n)}var S=e(d,[["render",i]]);export{v as __pageData,S as default};
