import{_ as p}from"./chunks/gallery-splash.defebc1e.js";import{_ as c}from"./chunks/BlazorTemplates.c176e71e.js";import{I as e}from"./chunks/iconify.2272db1b.js";import{o as l,c as r,d as n,e as a,u as t,a as o,b as s}from"./app.2aea72c0.js";const i={id:"blazor-server",class:"mt-12 ml-20 flex flex-col items-center"},u=o('<div class="relative bg-white py-4"><div class="mx-auto max-w-md px-4 text-center sm:max-w-3xl sm:px-6 lg:max-w-7xl lg:px-8"><p class="mt-2 text-3xl font-extrabold tracking-tight text-gray-900 sm:text-4xl">Blazor Server Tailwind Template</p><p class="mx-auto mt-5 max-w-prose text-xl text-gray-500"> Ultimate dev model &amp; UX ideal for low-latency Intranet environments </p></div></div><a href="https://blazor-server.jamstacks.net"><div class="block flex justify-center shadow hover:shadow-lg rounded py-1"><img class="p-4" src="https://raw.githubusercontent.com/ServiceStack/Assets/master/csharp-templates/blazor-server.png"></div></a><p><a href="/templates-blazor-components">ServiceStack.Blazor&#39;s Tailwind Components</a> also work flawlessly in <a href="https://learn.microsoft.com/en-us/aspnet/core/blazor/hosting-models?view=aspnetcore-6.0#blazor-server" target="_blank" rel="noopener noreferrer">Blazor Server Apps</a> which benefits from fast startup and exceptional responsiveness in low latency environments thanks to its architecture of running your App in a server session that only needs to propagate thin UI Virtual DOM updates to clients.</p><p>The Blazor Server App template offers a number compelling advantages over Blazor WASM, including:</p><ul><li>A superior dev model and debugging experience</li><li>Improved live-reload and faster iterative dev cycles</li><li>Full access to .NET Server functionality</li><li>Better start times &amp; UI responsiveness</li><li>Less complexity from unnecessary client project or pre-rendering solutions</li></ul><p>Although <a href="https://learn.microsoft.com/en-us/aspnet/core/blazor/hosting-models?view=aspnetcore-6.0#blazor-server" target="_blank" rel="noopener noreferrer">the limitations</a> of its highly-coupled stateful server rendering session architecture does make it a poor fit for most high latency Internet sites which we continue to recommend our <a href="/templates-blazor-tailwind">Blazor WASM project template</a> for.</p><p>To better showcase our growing Blazor functionality we&#39;ve created the new Blazor Gallery websites showcasing usage of available rich Blazor Components for rapidly develop beautiful Tailwind Web Apps:</p><div id="blazor-component-gallery" class="relative bg-white py-4"><div class="mx-auto max-w-md px-4 text-center sm:max-w-3xl sm:px-6 lg:max-w-7xl lg:px-8"><p class="mt-2 text-3xl font-extrabold tracking-tight text-gray-900 sm:text-4xl">Blazor Gallery</p><p class="mx-auto mt-5 max-w-prose text-xl text-gray-500"> Discover ServiceStack.Blazor Rich UI Components and Integrated Features </p></div></div><p><a href="https://blazor-gallery.servicestack.net" target="_blank" rel="noopener noreferrer"><img src="'+p+'" alt=""></a></p><p>As our components support both hosting models we&#39;re maintaining identical Gallery sites running on both <strong>Blazor Server</strong> and <strong>WASM</strong>:</p><div class="mb-16 mx-auto mt-5 max-w-md sm:flex sm:justify-center md:mt-8"><div class="rounded-md shadow"><a href="https://blazor-gallery.servicestack.net" class="flex w-full items-center justify-center rounded-md border border-transparent bg-indigo-600 px-8 py-3 text-base font-medium text-white hover:bg-indigo-700 md:py-4 md:px-10 md:text-lg hover:no-underline"> Blazor Server </a></div><div class="mt-3 rounded-md shadow sm:mt-0 sm:ml-3"><a href="https://blazor-gallery.jamstacks.net" class="flex w-full items-center justify-center rounded-md border border-transparent bg-white px-8 py-3 text-base font-medium text-indigo-600 hover:bg-gray-50 md:py-4 md:px-10 md:text-lg hover:no-underline"> Blazor WASM </a></div></div><p>For a closer look at ServiceStack.Blazor Components in action, download &amp; run them to see how good they&#39;ll run in your Environment:</p>',12),k={class:"flex flex-col"},d={href:"https://github.com/NetCoreApps/BlazorGallery",class:"text-xl text-gray-800"},m=n("span",null,"NetCoreApps/BlazorGallery",-1),h={href:"https://github.com/NetCoreApps/BlazorGalleryWasm",class:"mt-2 text-xl text-gray-800"},g=n("span",null,"NetCoreApps/BlazorGalleryWasm",-1),y=n("p",{class:"hide-h2"},null,-1),w=n("h2",{id:"getting-started",tabindex:"-1"},[s("Getting Started "),n("a",{class:"header-anchor",href:"#getting-started","aria-hidden":"true"},"#")],-1),b=n("p",null,"Customize and Download a new Blazor WASM Bootstrap project with your preferred project name:",-1),v=n("h3",{class:"text-center"},"Download new C# Blazor Project",-1),f=n("p",null,[s("Alternatively you can create & download a new Blazor Project with the "),n("a",{href:"/dotnet-new"},"x dotnet tool"),s(":")],-1),A=n("div",{class:"sh-copy cp flex cursor-pointer mb-3",onclick:"copy(this)"},[n("div",{class:"flex-grow bg-gray-800"},[n("div",{class:"pl-4 py-1 pb-1.5 align-middle whitespace-pre text-base text-gray-100"},[n("p",null,"x new blazor-server ProjectName")])]),n("div",{class:"flex"},[n("div",{class:"bg-green-600 text-white p-1.5 pb-0"},[n("svg",{class:"copied w-6 h-6",fill:"none",stroke:"currentColor",viewBox:"0 0 24 24",xmlns:"http://www.w3.org/2000/svg"},[n("path",{"stroke-linecap":"round","stroke-linejoin":"round","stroke-width":"2",d:"M5 13l4 4L19 7"})]),n("svg",{class:"nocopy w-6 h-6",title:"copy",fill:"none",stroke:"white",viewBox:"0 0 24 24",xmlns:"http://www.w3.org/2000/svg"},[n("path",{"stroke-linecap":"round","stroke-linejoin":"round","stroke-width":"1",d:"M8 7v8a2 2 0 002 2h6M8 7V5a2 2 0 012-2h4.586a1 1 0 01.707.293l4.414 4.414a1 1 0 01.293.707V15a2 2 0 01-2 2h-2M8 7H6a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2v-2"})])])])],-1),x=n("h2",{id:"universal-blazor-components",tabindex:"-1"},[s("Universal Blazor Components "),n("a",{class:"header-anchor",href:"#universal-blazor-components","aria-hidden":"true"},"#")],-1),z=n("p",null,[s("Blazor Server has become our preferred platform for Interactive "),n("strong",null,"Intranet"),s(" Apps which excels in "),n("strong",null,"low-latency environments"),s(" to enable a best-in-class responsive end-user UX that also offers a superior development experience in Visual Studio's live reload where it enables a fast iterative development workflow and good debugging experience.")],-1),S=n("p",null,[s("A fantastic property of Blazor is its support for multiple hosting modes which allows the same components from being able to run in the Browser with Blazor WASM or rendered on the Server with Blazor Server. But whilst Blazor is capable of it, this trait is typically conceded in most Apps with database access where it recommends using "),n("a",{href:"https://learn.microsoft.com/en-us/aspnet/core/blazor/blazor-server-ef-core?view=aspnetcore-7.0",target:"_blank",rel:"noopener noreferrer"},"EF Core directly in Blazor components"),s(" - effectively prohibiting reuse in Blazor WASM should you ever want to utilize Blazor's preferred hosting model for hosting your Blazor App's on the Internet.")],-1),B=n("div",{class:"my-8 flex justify-center"},[n("iframe",{class:"video-hd",src:"https://www.youtube.com/embed/66DgLHExC9E",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture",allowfullscreen:""})],-1),_=o(`<h2 id="blazing-fast-networkless-apis" tabindex="-1">Blazing Fast Networkless APIs <a class="header-anchor" href="#blazing-fast-networkless-apis" aria-hidden="true">#</a></h2><p>Whilst better performing, having Blazor components access DB&#39;s directly encourages a more tightly-coupled and less reusable &amp; testable architecture than the traditional well-defined API dev model used in client/server Mobile &amp; Desktop Apps or Web SPA Apps like WASM.</p><p>To achieve the best of both worlds, we&#39;ve enabled support for utilizing the In Process <a href="/service-gateway">Service Gateway</a> in Blazor Server Apps which lets you retain the traditional client/server dev model for invoking your Server APIs <strong>In Process</strong> - avoiding any serialization, HTTP networking or even Kestrel middleware overhead to invoke your APIs directly!</p><p>This enables using the <strong>exact same source code</strong> to call APIs in Blazor Server and WASM which allows us to develop reusable Blazor Components to invoke the same Server APIs that serve Web, Mobile and Desktop Apps in Blazor Server Apps. Where instead of using HttpClient to invoke your APIs, they&#39;re invoked directly from a C# method which will preserve its StackTrace where you&#39;ll be able to track the API call down to the Blazor UI component calling it.</p><p>ServiceStack&#39;s <a href="/api-design">Message-based API Design</a> makes it possible for all API calls in ServiceStack.Blazor components and project templates to be routed through these 2 methods:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">interface</span> <span class="token class-name">IServiceGatewayAsync</span>
<span class="token punctuation">{</span>
    <span class="token return-type class-name">Task<span class="token punctuation">&lt;</span>TResponse<span class="token punctuation">&gt;</span></span> <span class="token generic-method"><span class="token function">SendAsync</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>TResponse<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token class-name"><span class="token keyword">object</span></span> dto<span class="token punctuation">,</span> <span class="token class-name">CancellationToken</span> ct<span class="token operator">=</span><span class="token keyword">default</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token comment">//...</span>
<span class="token punctuation">}</span>

<span class="token keyword">public</span> <span class="token keyword">interface</span> <span class="token class-name">IServiceGatewayFormAsync</span>
<span class="token punctuation">{</span>
    <span class="token return-type class-name">Task<span class="token punctuation">&lt;</span>TResponse<span class="token punctuation">&gt;</span></span> <span class="token generic-method"><span class="token function">SendFormAsync</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>TResponse<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token class-name"><span class="token keyword">object</span></span> dto<span class="token punctuation">,</span> <span class="token class-name">MultipartFormDataContent</span> form<span class="token punctuation">,</span> <span class="token class-name">CancellationToken</span> ct<span class="token operator">=</span><span class="token keyword">default</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The <code>SendFormAsync</code> API is a new method added to support multi-part API requests with File Uploads</p></div><p>Which allows the HTTP <code>JsonApiClient</code> and networkless <code>InProcessGateway</code> clients to be used interchangeably. By default Blazor Server Apps now use the InProcess Gateway but can be switched over to invoke APIs using the HTTP <code>JsonApiClient</code> with:</p><div class="language-csharp"><pre><code>BlazorConfig<span class="token punctuation">.</span><span class="token function">Set</span><span class="token punctuation">(</span><span class="token keyword">new</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
    UseInProcessClient <span class="token operator">=</span> <span class="token boolean">false</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><p>Which changes all <code>Api*</code> methods in Blazor components and Pages inheriting ServiceStack.Blazor&#39;s <a href="https://reference.servicestack.net/api/ServiceStack.Blazor/BlazorComponentBase" target="_blank" rel="noopener noreferrer">BlazorComponentBase</a> to use the registered <code>JsonApiClient</code> client.</p><p>Other components can access both the InProcess Gateway or <code>JsonApiClient</code> by injecting the <code>IClientFactory</code> dependency into their components, e.g:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">MyComponent</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">ComponentBase</span></span>
<span class="token punctuation">{</span>
    <span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Inject</span></span><span class="token punctuation">]</span> <span class="token keyword">public</span> <span class="token return-type class-name">IClientFactory<span class="token punctuation">?</span></span> ClientFactory <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>

    <span class="token keyword">public</span> <span class="token return-type class-name">IServiceGateway</span> Gateway <span class="token operator">=&gt;</span> ClientFactory<span class="token operator">!</span><span class="token punctuation">.</span><span class="token function">GetGateway</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token keyword">public</span> <span class="token return-type class-name">JsonApiClient</span> Client <span class="token operator">=&gt;</span> ClientFactory<span class="token operator">!</span><span class="token punctuation">.</span><span class="token function">GetClient</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre></div><p>This capability is what has made it possible for high-level &quot;API-enabled&quot; components like <a href="https://blazor-gallery.jamstacks.net/grid" target="_blank" rel="noopener noreferrer">AutoQuery Grids</a> and <a href="https://blazor-gallery.jamstacks.net/gallery/autoform" target="_blank" rel="noopener noreferrer">AutoForm</a> to support both Blazor Server and Blazor WASM utilizing the most efficient API client available to its platform.</p><p>The Blazor Gallery websites themselves are also good demonstrations of being able to run <strong>entire Web Apps</strong> in both Blazor Server and WASM, with all development being done with Blazor Server to take advantage of its superior iterative dev model then a script is used to &quot;export&quot; all pages to an identical Blazor WASM project.</p><h2 id="api-and-apiasync-methods" tabindex="-1">Api and ApiAsync methods <a class="header-anchor" href="#api-and-apiasync-methods" aria-hidden="true">#</a></h2><p>.NET was originally conceived to use Exceptions for error control flow however there&#39;s been a tendency in modern languages &amp; libraries to shun Exceptions and return errors as normal values, an approach we believe is a more flexible &amp; ergonomic way to handle API responses.</p><h3 id="the-apiresult-way" tabindex="-1">The ApiResult way <a class="header-anchor" href="#the-apiresult-way" aria-hidden="true">#</a></h3><p>The <code>Api(Request)</code> and <code>ApiAsync(Request)</code> APIs returns a typed <code>ApiResult&lt;Response&gt;</code> Value Result encapsulating either a Typed Response or a structured API Error populated in <code>ResponseStatus</code> allowing you to handle API responses programmatically without <code>try/catch</code> handling:</p><p>The below example code to create a new Booking:</p><div class="language-csharp"><pre><code><span class="token class-name">CreateBooking</span> request <span class="token operator">=</span> <span class="token keyword">new</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token class-name">ApiResult<span class="token punctuation">&lt;</span>IdResponse<span class="token punctuation">&gt;</span></span> api <span class="token operator">=</span> <span class="token keyword">new</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token keyword">async</span> <span class="token return-type class-name">Task</span> <span class="token function">OnSubmit</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
<span class="token punctuation">{</span>
    api <span class="token operator">=</span> <span class="token keyword">await</span> Client<span class="token punctuation">.</span><span class="token function">ApiAsync</span><span class="token punctuation">(</span>request<span class="token punctuation">)</span><span class="token punctuation">;</span>

    <span class="token keyword">if</span> <span class="token punctuation">(</span>api<span class="token punctuation">.</span>Succeeded<span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token keyword">await</span> done<span class="token punctuation">.</span><span class="token function">InvokeAsync</span><span class="token punctuation">(</span>api<span class="token punctuation">.</span>Response<span class="token operator">!</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        request <span class="token operator">=</span> <span class="token keyword">new</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>Which despite its terseness handles both <strong>success</strong> and <strong>error</strong> API responses, <strong>if successful</strong> it invokes the <code>done()</code> callback notifying its parent of the new Booking API Response before resetting the Form&#39;s data model with a new Request DTO.</p><p>Upon <strong>failure</strong> the error response is populated in <code>api.Error</code> which binds to the UI via Blazor&#39;s <code>&lt;CascadingValue Value=@api.Error&gt;</code> to propagate it to all its child components in order to show contextual validation errors next to their respective Input controls.</p><h3 id="public-pages-components" tabindex="-1">Public Pages &amp; Components <a class="header-anchor" href="#public-pages-components" aria-hidden="true">#</a></h3><p>To reduce boiler plate, your Blazor Pages &amp; components can inherit the templates local <a href="https://github.com/NetCoreTemplates/blazor-wasm/blob/main/MyApp.Client/AppComponentBase.cs" target="_blank" rel="noopener noreferrer">AppComponentBase.cs</a> which inherits <code>BlazorComponentBase</code> that gets injected with the <code>IClientFactory</code> and provides convenient access to most common APIs:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">BlazorComponentBase</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">ComponentBase</span><span class="token punctuation">,</span> <span class="token class-name">IHasJsonApiClient</span></span>
<span class="token punctuation">{</span>
    <span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Inject</span></span><span class="token punctuation">]</span> <span class="token keyword">public</span> <span class="token return-type class-name">IClientFactory<span class="token punctuation">?</span></span> ClientFactory <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token return-type class-name">IServiceGateway</span> Gateway <span class="token operator">=&gt;</span> ClientFactory<span class="token operator">!</span><span class="token punctuation">.</span><span class="token function">GetGateway</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token keyword">public</span> <span class="token return-type class-name">JsonApiClient</span> Client <span class="token operator">=&gt;</span> ClientFactory<span class="token operator">!</span><span class="token punctuation">.</span><span class="token function">GetClient</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

    <span class="token keyword">public</span> <span class="token keyword">virtual</span> <span class="token return-type class-name">Task<span class="token punctuation">&lt;</span>ApiResult<span class="token punctuation">&lt;</span>TResponse<span class="token punctuation">&gt;</span><span class="token punctuation">&gt;</span></span> <span class="token generic-method"><span class="token function">ApiAsync</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>TResponse<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token class-name">IReturn<span class="token punctuation">&lt;</span>TResponse<span class="token punctuation">&gt;</span></span> request<span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token return-type class-name">UseGateway 
        <span class="token punctuation">?</span></span> Gateway<span class="token punctuation">.</span><span class="token function">ManagedApiAsync</span><span class="token punctuation">(</span>request<span class="token punctuation">)</span>
        <span class="token punctuation">:</span> Client<span class="token punctuation">.</span><span class="token function">ManagedApiAsync</span><span class="token punctuation">(</span>request<span class="token punctuation">)</span><span class="token punctuation">;</span>

    <span class="token keyword">public</span> <span class="token keyword">virtual</span> <span class="token return-type class-name">Task<span class="token punctuation">&lt;</span>ApiResult<span class="token punctuation">&lt;</span>EmptyResponse<span class="token punctuation">&gt;</span><span class="token punctuation">&gt;</span></span> <span class="token function">ApiAsync</span><span class="token punctuation">(</span><span class="token class-name">IReturnVoid</span> request<span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">/*...*/</span>
    <span class="token keyword">public</span> <span class="token keyword">virtual</span> <span class="token return-type class-name">Task<span class="token punctuation">&lt;</span>TResponse<span class="token punctuation">&gt;</span></span> <span class="token generic-method"><span class="token function">SendAsync</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>TResponse<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token class-name">IReturn<span class="token punctuation">&lt;</span>TResponse<span class="token punctuation">&gt;</span></span> request<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token keyword">public</span> <span class="token keyword">virtual</span> <span class="token return-type class-name">Task<span class="token punctuation">&lt;</span>IHasErrorStatus<span class="token punctuation">&gt;</span></span> <span class="token generic-method"><span class="token function">ApiAsync</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>Model<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token class-name"><span class="token keyword">object</span></span> request<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token keyword">public</span> <span class="token keyword">virtual</span> <span class="token return-type class-name">Task<span class="token punctuation">&lt;</span>ApiResult<span class="token punctuation">&lt;</span>Model<span class="token punctuation">&gt;</span><span class="token punctuation">&gt;</span></span> <span class="token generic-method"><span class="token function">ApiFormAsync</span><span class="token generic class-name"><span class="token punctuation">&lt;</span>Model<span class="token punctuation">&gt;</span></span></span><span class="token punctuation">(</span><span class="token class-name"><span class="token keyword">object</span></span> requestDto<span class="token punctuation">,</span> <span class="token class-name">MultipartFormDataContent</span> request<span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre></div><h3 id="protected-pages-components" tabindex="-1">Protected Pages &amp; Components <a class="header-anchor" href="#protected-pages-components" aria-hidden="true">#</a></h3><p>Pages and Components requiring Authentication should inherit from <a href="https://github.com/NetCoreTemplates/blazor-wasm/blob/main/MyApp.Client/AppComponentBase.cs" target="_blank" rel="noopener noreferrer">AppAuthComponentBase</a> instead which integrates with Blazor&#39;s Authentication Model to provide access to the currently authenticated user:</p><div class="language-csharp"><pre><code><span class="token keyword">public</span> <span class="token keyword">abstract</span> <span class="token keyword">class</span> <span class="token class-name">AppAuthComponentBase</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">AppComponentBase</span></span>
<span class="token punctuation">{</span>
    <span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">CascadingParameter</span></span><span class="token punctuation">]</span>
    <span class="token keyword">protected</span> <span class="token return-type class-name">Task<span class="token punctuation">&lt;</span>AuthenticationState<span class="token punctuation">&gt;</span><span class="token punctuation">?</span></span> AuthenticationStateTask <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>

    <span class="token keyword">protected</span> <span class="token return-type class-name"><span class="token keyword">bool</span></span> HasInit <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>

    <span class="token keyword">protected</span> <span class="token return-type class-name"><span class="token keyword">bool</span></span> IsAuthenticated <span class="token operator">=&gt;</span> User<span class="token punctuation">?.</span>Identity<span class="token punctuation">?.</span>IsAuthenticated <span class="token operator">??</span> <span class="token boolean">false</span><span class="token punctuation">;</span>

    <span class="token keyword">protected</span> <span class="token return-type class-name">ClaimsPrincipal<span class="token punctuation">?</span></span> User <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>

    <span class="token keyword">protected</span> <span class="token keyword">override</span> <span class="token keyword">async</span> <span class="token return-type class-name">Task</span> <span class="token function">OnParametersSetAsync</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        <span class="token class-name"><span class="token keyword">var</span></span> state <span class="token operator">=</span> <span class="token keyword">await</span> AuthenticationStateTask<span class="token operator">!</span><span class="token punctuation">;</span>
        User <span class="token operator">=</span> state<span class="token punctuation">.</span>User<span class="token punctuation">;</span>
        HasInit <span class="token operator">=</span> <span class="token boolean">true</span><span class="token punctuation">;</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><h2 id="benefits-of-shared-dtos" tabindex="-1">Benefits of Shared DTOs <a class="header-anchor" href="#benefits-of-shared-dtos" aria-hidden="true">#</a></h2><p>Typically with Web Apps, our client is using a different language to C#, so an equivalent request DTOs need to be generated for the client.</p><h3 id="typescript-example" tabindex="-1">TypeScript Example <a class="header-anchor" href="#typescript-example" aria-hidden="true">#</a></h3><p>For example, TypeScript generated DTOs still give us typed end-to-end services with the help of tooling like <a href="/add-servicestack-reference">Add ServiceStack Reference</a></p><div class="language-csharp"><pre><code><span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Route</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;/hello/{Name}&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">Hello</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">IReturn<span class="token punctuation">&lt;</span>HelloResponse<span class="token punctuation">&gt;</span></span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> Name <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">HelloResponse</span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> Result <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>Turns into:</p><div class="language-typescript"><pre><code><span class="token comment">// @Route(&quot;/hello/{Name}&quot;)</span>
<span class="token keyword">export</span> <span class="token keyword">class</span> <span class="token class-name">Hello</span> <span class="token keyword">implements</span> <span class="token class-name">IReturn<span class="token operator">&lt;</span>HelloResponse<span class="token operator">&gt;</span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> name<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span>

    <span class="token keyword">public</span> <span class="token function">constructor</span><span class="token punctuation">(</span>init<span class="token operator">?</span><span class="token operator">:</span> Partial<span class="token operator">&lt;</span>Hello<span class="token operator">&gt;</span><span class="token punctuation">)</span> <span class="token punctuation">{</span> <span class="token punctuation">(</span>Object <span class="token keyword">as</span> <span class="token builtin">any</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">assign</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">,</span> init<span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token function">getTypeName</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span> <span class="token keyword">return</span> <span class="token string">&#39;Hello&#39;</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token function">getMethod</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span> <span class="token keyword">return</span> <span class="token string">&#39;POST&#39;</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
    <span class="token keyword">public</span> <span class="token function">createResponse</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span> <span class="token keyword">return</span> <span class="token keyword">new</span> <span class="token class-name">HelloResponse</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token keyword">export</span> <span class="token keyword">class</span> <span class="token class-name">HelloResponse</span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> result<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span>
    <span class="token keyword">public</span> responseStatus<span class="token operator">:</span> ResponseStatus<span class="token punctuation">;</span>

    <span class="token keyword">public</span> <span class="token function">constructor</span><span class="token punctuation">(</span>init<span class="token operator">?</span><span class="token operator">:</span> Partial<span class="token operator">&lt;</span>HelloResponse<span class="token operator">&gt;</span><span class="token punctuation">)</span> <span class="token punctuation">{</span> <span class="token punctuation">(</span>Object <span class="token keyword">as</span> <span class="token builtin">any</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">assign</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">,</span> init<span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>When Request or Response DTOs changes during development, the client DTOs need to be regenerated using a command like <a href="./add-servicestack-reference.html#simple-command-line-utilities"><code>x csharp</code></a>.</p><h3 id="blazor-server-example" tabindex="-1">Blazor Server Example <a class="header-anchor" href="#blazor-server-example" aria-hidden="true">#</a></h3><p>Developing your Blazor Server UI however, you just change your shared request/response DTO in the shared <code>ServiceModel</code> project, and both your client and server compile against the same request/response DTO classes. This eliminates the need for any additional step.</p><p>In the <code>ServiceModel</code> project, we still have:</p><div class="language-csharp"><pre><code><span class="token punctuation">[</span><span class="token attribute"><span class="token class-name">Route</span><span class="token attribute-arguments"><span class="token punctuation">(</span><span class="token string">&quot;/hello/{Name}&quot;</span><span class="token punctuation">)</span></span></span><span class="token punctuation">]</span>
<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">Hello</span> <span class="token punctuation">:</span> <span class="token type-list"><span class="token class-name">IReturn<span class="token punctuation">&lt;</span>HelloResponse<span class="token punctuation">&gt;</span></span></span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> Name <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token keyword">public</span> <span class="token keyword">class</span> <span class="token class-name">HelloResponse</span>
<span class="token punctuation">{</span>
    <span class="token keyword">public</span> <span class="token return-type class-name"><span class="token keyword">string</span></span> Result <span class="token punctuation">{</span> <span class="token keyword">get</span><span class="token punctuation">;</span> <span class="token keyword">set</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>Which the Blazor C# App can use directly in its <strong>.razor</strong> pages:</p><div class="language-csharp"><pre><code>@code <span class="token punctuation">{</span>
    <span class="token class-name">Hello</span> request <span class="token operator">=</span> <span class="token keyword">new</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token punctuation">{</span>
        Name <span class="token operator">=</span> <span class="token string">&quot;Blazor WASM&quot;</span>
    <span class="token punctuation">}</span><span class="token punctuation">;</span>

    <span class="token class-name">ApiResult<span class="token punctuation">&lt;</span>HelloResponse<span class="token punctuation">&gt;</span></span> api <span class="token operator">=</span> <span class="token keyword">new</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

    <span class="token keyword">protected</span> <span class="token keyword">override</span> <span class="token keyword">async</span> <span class="token return-type class-name">Task</span> <span class="token function">OnInitializedAsync</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token keyword">await</span> <span class="token function">submit</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

    <span class="token keyword">async</span> <span class="token return-type class-name">Task</span> <span class="token function">submit</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> api <span class="token operator">=</span> <span class="token keyword">await</span> <span class="token function">ApiAsync</span><span class="token punctuation">(</span>request<span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre></div><h2 id="servicestack-blazor-components" tabindex="-1">ServiceStack.Blazor Components <a class="header-anchor" href="#servicestack-blazor-components" aria-hidden="true">#</a></h2><p>The <a href="/templates-blazor-components">ServiceStack.Blazor Components library</a> contains integrated functionality for Blazor including API-enabled base components, HTML Utils and Tailwind UI Input components heavily utilized throughout the template.</p>`,44),G='{"title":"Getting Started","description":"","frontmatter":{},"headers":[{"level":2,"title":"Getting Started","slug":"getting-started"},{"level":2,"title":"Universal Blazor Components","slug":"universal-blazor-components"},{"level":2,"title":"Blazing Fast Networkless APIs","slug":"blazing-fast-networkless-apis"},{"level":2,"title":"Api and ApiAsync methods","slug":"api-and-apiasync-methods"},{"level":3,"title":"The ApiResult way","slug":"the-apiresult-way"},{"level":3,"title":"Public Pages & Components","slug":"public-pages-components"},{"level":3,"title":"Protected Pages & Components","slug":"protected-pages-components"},{"level":2,"title":"Benefits of Shared DTOs","slug":"benefits-of-shared-dtos"},{"level":3,"title":"TypeScript Example","slug":"typescript-example"},{"level":3,"title":"Blazor Server Example","slug":"blazor-server-example"},{"level":2,"title":"ServiceStack.Blazor Components","slug":"servicestack-blazor-components"}],"relativePath":"templates-blazor-server.md"}',T={},F=Object.assign(T,{__name:"templates-blazor-server",setup(C){return(I,P)=>(l(),r("div",null,[n("div",i,[n("div",null,[a(t(e),{icon:"simple-icons:blazor",class:"w-24 h-24 text-purple-600 mr-8"}),a(t(e),{icon:"logos:tailwindcss-icon",class:"w-28 h-28"})])]),u,n("div",k,[n("a",d,[a(t(e),{icon:"simple-icons:github",class:"w-6 h-6 mr-2 align-text-bottom"}),m]),n("a",h,[a(t(e),{icon:"simple-icons:github",class:"w-6 h-6 mr-2 align-text-bottom"}),g])]),y,w,b,v,a(c,{class:"pb-8"}),f,A,x,z,S,B,_]))}});export{G as __pageData,F as default};
