import"./chunks/iconify.2272db1b.js";import{_ as e}from"./chunks/VueTailwindTemplates.474d377f.js";import{_ as o}from"./chunks/VueComponentGallery.3bc82364.js";import{o as p,c as i,e as a,d as s,b as n,a as t}from"./app.2aea72c0.js";const r=s("p",null,[s("strong",null,"@servicestack/vue"),n(" is a component library for Vue.js and Tailwind CSS offering a wide range of beautifully designed, highly productive components that can be easily customized to fit into Tailwind's expert design system whose deep integration with your ServiceStack APIs unlocks end-to-end productivity. With a focus on performance, accessibility and modern design, this library is the perfect choice for developers looking to rapidly develop fast, beautiful modern Vue.js Tailwind websites.")],-1),c=s("div",{class:"flex justify-center"},[s("iframe",{class:"video-hd",src:"https://www.youtube.com/embed/YIa0w6whe2U",frameborder:"0",allow:"autoplay; encrypted-media",allowfullscreen:""})],-1),l=s("h2",{id:"getting-started",tabindex:"-1"},[n("Getting Started "),s("a",{class:"header-anchor",href:"#getting-started","aria-hidden":"true"},"#")],-1),u=s("p",null,[n("Starting with one of ServiceStack's Vue + Tailwind Project templates below is the easiest way to get started which are pre-configured with "),s("strong",null,"@servicestack/client"),n(" and "),s("strong",null,"@servicestack/vue"),n(" libraries:")],-1),d=t('<p>All Razor Pages and MVC templates utilize the JS Modules support in modern browsers to avoid any needing any npm build system, for access to more advanced npm library features and to learn about Jamstack CDN and SSG benefits checkout <a href="/templates-jamstack">Jamstack Templates docs</a>.</p><h3 id="vue-js-razor-pages-mvc-templates-overview" tabindex="-1">Vue.js Razor Pages &amp; MVC Templates Overview <a class="header-anchor" href="#vue-js-razor-pages-mvc-templates-overview" aria-hidden="true">#</a></h3><p>To help choosing which template to start with, here&#39;s a quick summary of their differences:</p><ul><li><strong>vue-mjs</strong> - Flagship Vue.mjs template complete with <a href="/ormlite/">OrmLite</a>, <a href="/autoquery">AutoQuery</a>, <a href="https://htmx.org/attributes/hx-boost/" target="_blank" rel="noopener noreferrer">boosted htmx</a> links &amp; <a href="https://vue-mjs.web-templates.io/blog/prerendering" target="_blank" rel="noopener noreferrer">static pre-rendered blogs</a></li><li><strong>razor-tailwind</strong> - Simpler Razor Pages Template without a configured DB or static pre-rendered blogs</li><li><strong>mvc-tailwind</strong> - Want to use MVC Identity Auth and Entity Framework</li><li><strong>web-tailwind</strong> - Empty tailwind template who don&#39;t want to use Razor Pages or MVC</li><li><strong>vue-vite</strong> - Want to use TypeScript in a simpler JAMStack Vite SPA App</li><li><strong>vue-ssg</strong> - Want to use TypeScript in an advanced JAMStack Vite SSG App</li></ul><p>For a more in-depth look we&#39;ve created an overview covering the differences between the Razor Pages &amp; MVC templates:</p>',5),k=s("div",{class:"flex justify-center"},[s("iframe",{class:"video-hd",src:"https://www.youtube.com/embed/SyppvQB7IPs",frameborder:"0",allow:"autoplay; encrypted-media",allowfullscreen:""})],-1),g=t(`<h3 id="manual-installation" tabindex="-1">Manual Installation <a class="header-anchor" href="#manual-installation" aria-hidden="true">#</a></h3><p>Alternatively it can be added to existing Vue SPA Apps by installing via npm:</p><div class="language-bash"><pre><code>$ <span class="token function">npm</span> <span class="token function">install</span> @servicestack/vue
</code></pre></div><p>Where it will also install its <strong>vue</strong> and <strong>@servicestack/client</strong> dependencies.</p><h3 id="installation-less-option" tabindex="-1">Installation-less option <a class="header-anchor" href="#installation-less-option" aria-hidden="true">#</a></h3><p>Alternatively you can take advantage of modern browsers <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Guide/Modules" target="_blank" rel="noopener noreferrer">JS Modules</a> support to use these libraries without installation by registering an <a href="https://developer.mozilla.org/en-US/docs/Web/HTML/Element/script/type/importmap" target="_blank" rel="noopener noreferrer">importmap</a> to define where it should load the ESM builds of these libraries from, e.g:</p><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span> <span class="token attr-name">type</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>importmap<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token script"><span class="token language-javascript">
<span class="token punctuation">{</span>
    <span class="token string-property property">&quot;imports&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>
        <span class="token string-property property">&quot;vue&quot;</span><span class="token operator">:</span>                  <span class="token string">&quot;https://unpkg.com/vue@3/dist/vue.esm-browser.prod.js&quot;</span><span class="token punctuation">,</span>
        <span class="token string-property property">&quot;@servicestack/client&quot;</span><span class="token operator">:</span> <span class="token string">&quot;https://unpkg.com/@servicestack/client@2/dist/servicestack-client.min.mjs&quot;</span><span class="token punctuation">,</span>
        <span class="token string-property property">&quot;@servicestack/vue&quot;</span><span class="token operator">:</span>    <span class="token string">&quot;https://unpkg.com/@servicestack/vue@3/dist/servicestack-vue.min.mjs&quot;</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">&gt;</span></span>
</code></pre></div><p>For intranet Web Apps that need to work without internet access, save and reference local copies of these libraries, e.g:</p><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span> <span class="token attr-name">type</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>importmap<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token script"><span class="token language-javascript">
<span class="token punctuation">{</span>
    <span class="token string-property property">&quot;imports&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>
        <span class="token string-property property">&quot;vue&quot;</span><span class="token operator">:</span>                  <span class="token string">&quot;/lib/mjs/vue.mjs&quot;</span><span class="token punctuation">,</span>
        <span class="token string-property property">&quot;@servicestack/client&quot;</span><span class="token operator">:</span> <span class="token string">&quot;/lib/mjs/servicestack-client.mjs&quot;</span><span class="token punctuation">,</span>
        <span class="token string-property property">&quot;@servicestack/vue&quot;</span><span class="token operator">:</span>    <span class="token string">&quot;/lib/mjs/servicestack-vue.mjs&quot;</span>
    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">&gt;</span></span>
</code></pre></div><h3 id="html-importmap" tabindex="-1">@Html.ImportMap <a class="header-anchor" href="#html-importmap" aria-hidden="true">#</a></h3><p>Razor Pages or MVC Apps can use the <code>Html.ImportMaps()</code> to use local debug builds during development and optimal CDN hosted minified production builds in production:</p><div class="language-csharp"><pre><code>@Html<span class="token punctuation">.</span><span class="token function">ImportMap</span><span class="token punctuation">(</span><span class="token keyword">new</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
<span class="token punctuation">{</span>
    <span class="token punctuation">[</span><span class="token string">&quot;vue&quot;</span><span class="token punctuation">]</span>                  <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token string">&quot;/lib/mjs/vue.mjs&quot;</span><span class="token punctuation">,</span>                 <span class="token string">&quot;https://unpkg.com/vue@3/dist/vue.esm-browser.prod.js&quot;</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
    <span class="token punctuation">[</span><span class="token string">&quot;@servicestack/client&quot;</span><span class="token punctuation">]</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token string">&quot;/lib/mjs/servicestack-client.mjs&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;https://unpkg.com/@servicestack/client@2/dist/servicestack-client.min.mjs&quot;</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
    <span class="token punctuation">[</span><span class="token string">&quot;@servicestack/vue&quot;</span><span class="token punctuation">]</span>    <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token string">&quot;/lib/mjs/servicestack-vue.mjs&quot;</span><span class="token punctuation">,</span>    <span class="token string">&quot;https://unpkg.com/@servicestack/vue@3/dist/servicestack-vue.min.mjs&quot;</span><span class="token punctuation">)</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><blockquote><p>It&#39;s recommended to use exact versions to eliminate redirect latencies and to match the local version your App was developed against</p></blockquote><h4 id="polyfill-for-safari" tabindex="-1">Polyfill for Safari <a class="header-anchor" href="#polyfill-for-safari" aria-hidden="true">#</a></h4><p>Unfortunately Safari is the last modern browser to <a href="https://caniuse.com/import-maps" target="_blank" rel="noopener noreferrer">support import maps</a> which is only now in Technical Preview. Luckily this feature can be polyfilled with the pre-configured <a href="https://github.com/guybedford/es-module-shims" target="_blank" rel="noopener noreferrer">ES Module Shims</a>:</p><div class="language-html"><pre><code>@if (Context.Request.Headers.UserAgent.Any(x =&gt; x.Contains(&quot;Safari&quot;) &amp;&amp; !x.Contains(&quot;Chrome&quot;)))
{
    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span> <span class="token attr-name">async</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>https://ga.jspm.io/npm:es-module-shims@1.6.3/dist/es-module-shims.js<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token script"></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">&gt;</span></span>
}
</code></pre></div><h3 id="registration" tabindex="-1">Registration <a class="header-anchor" href="#registration" aria-hidden="true">#</a></h3><p>Then register the <code>@servicestack/vue</code> component library with your Vue app with:</p><div class="language-js"><pre><code><span class="token keyword">import</span> <span class="token punctuation">{</span> JsonApiClient <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&quot;@servicestack/client&quot;</span>
<span class="token keyword">import</span> ServiceStackVue <span class="token keyword">from</span> <span class="token string">&quot;@servicestack/vue&quot;</span>

<span class="token keyword">const</span> client <span class="token operator">=</span> JsonApiClient<span class="token punctuation">.</span><span class="token function">create</span><span class="token punctuation">(</span><span class="token punctuation">)</span>

<span class="token keyword">const</span> app <span class="token operator">=</span> <span class="token function">createApp</span><span class="token punctuation">(</span>component<span class="token punctuation">,</span> props<span class="token punctuation">)</span>
app<span class="token punctuation">.</span><span class="token function">provide</span><span class="token punctuation">(</span><span class="token string">&#39;client&#39;</span><span class="token punctuation">,</span> client<span class="token punctuation">)</span>
app<span class="token punctuation">.</span><span class="token function">use</span><span class="token punctuation">(</span>ServiceStackVue<span class="token punctuation">)</span>

<span class="token comment">//...</span>
app<span class="token punctuation">.</span><span class="token function">mount</span><span class="token punctuation">(</span><span class="token string">&#39;#app&#39;</span><span class="token punctuation">)</span>
</code></pre></div><p>The <strong>client</strong> instance is used by API-enabled components to call your APIs using the <a href="/routing#json-api-pre-defined-route">/api predefined route</a>. ServiceStack Apps not running on .NET 6+ or have the <strong>/api</strong> route disabled should use <code>JsonServiceClient</code> instead:</p><div class="language-js"><pre><code><span class="token keyword">const</span> client <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">JsonServiceClient</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
</code></pre></div><h3 id="not-using-vue-router" tabindex="-1">Not using Vue Router <a class="header-anchor" href="#not-using-vue-router" aria-hidden="true">#</a></h3><p>Non SPA Vue Apps that don&#39;t use <a href="https://router.vuejs.org" target="_blank" rel="noopener noreferrer">Vue Router</a> should register a replacement <code>&lt;router-link&gt;</code> component that uses the browser&#39;s native navigation in <a href="/vue/gallery/navigation">navigational components</a>:</p><div class="language-js"><pre><code>app<span class="token punctuation">.</span><span class="token function">component</span><span class="token punctuation">(</span><span class="token string">&#39;RouterLink&#39;</span><span class="token punctuation">,</span> ServiceStackVue<span class="token punctuation">.</span><span class="token function">component</span><span class="token punctuation">(</span><span class="token string">&#39;RouterLink&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
</code></pre></div>`,24),_='{"title":"@servicestack/vue","description":"","frontmatter":{"title":"@servicestack/vue"},"headers":[{"level":2,"title":"Getting Started","slug":"getting-started"},{"level":3,"title":"Vue.js Razor Pages & MVC Templates Overview","slug":"vue-js-razor-pages-mvc-templates-overview"},{"level":3,"title":"Manual Installation","slug":"manual-installation"},{"level":3,"title":"Installation-less option","slug":"installation-less-option"},{"level":3,"title":"@Html.ImportMap","slug":"html-importmap"},{"level":3,"title":"Registration","slug":"registration"},{"level":3,"title":"Not using Vue Router","slug":"not-using-vue-router"}],"relativePath":"vue/index.md"}',m={},S=Object.assign(m,{__name:"index",setup(h){return(v,f)=>(p(),i("div",null,[r,a(o),c,l,u,a(e,{class:"mb-8"}),d,k,g]))}});export{_ as __pageData,S as default};
